import React, { Component } from 'react';
import { Container, Avatar, Typography, TextField, Button } from '@material-ui/core';
import styleForm from '../estilos/estiloForm';
import Icono from "@material-ui/icons/LockOutlined";
import { compose } from 'recompose';
import { consumerFirebase } from '../../server';

class Login extends Component {
    state={
        Firebase:null,
        usuario:{
            correo:"",
            contraseña:""
        }
    }
    cambiarState=e=>{
        let UsuarioLogin=Object.assign({},this.state.usuario);
        UsuarioLogin[e.target.name]=e.target.value;
        this.setState({
            usuario:UsuarioLogin
        })
    }
    render() {
        return (
            <Container maxWidth="xs">
                <div style={styleForm.paper}>
                    <Avatar style={styleForm.Avatar}>
                        <Icono></Icono>
                    </Avatar>
                    <Typography component="h1" variant="h5">Ingresa tus datos</Typography>
                    <form style={styleForm.formulario}>
                        <TextField variant="outlined" fullWidth label="Correo" onChange={this.cambiarState} name="correo" margin="normal"/>
                        <TextField variant="outlined" fullWidth label="Contraseña" onChange={this.cambiarState} margin="normal" type="password" name="contraseña"/>
                        <Button fullWidth variant="contained" color="primary"  type="submit">Iniciar</Button>
                    </form>
                </div>
            </Container>
        );
    }
}

export default compose(consumerFirebase)(Login);
