import React, {useRef} from 'react';
import CanvasDraw from 'react-canvas-draw';

function Tablero (){
  
        const primerDibujo =useRef(null);
        const segundoDibujo =useRef(null);
        const guardar=()=>{ 
        const imagen = primerDibujo.current.getSaveData();
        console.log(imagen);
        segundoDibujo.current.loadSaveData(imagen,false);
        }
        const borrar=()=>{
            primerDibujo.current.clear();
        }
        const volver=()=>{
            primerDibujo.current.undo();
        }
        return (
            <div className="ed-grid" style="background:red">
                <br></br>
                <button onClick={guardar}>Guardar</button>
                <button onClick={borrar}>Borrar</button>
                <button onClick={volver}>Volver</button>
                <CanvasDraw brushColor="#130f40" canvasHeight={"500px"} canvasWidth={"50%"} brushRadius={1} ref={primerDibujo} />
                <br></br>
                <br></br>
                <CanvasDraw ref={segundoDibujo} hideGrid="true" disabled="true" canvasHeight={"900px"} canvasWidth={"100%"} brushColor="#130f40" brushRadius={1} style={{border:"1px solid black"}} />
                <br></br>
            </div>
        );
}

export default Tablero;
