import React, { Component } from 'react';
import { Container, Avatar, Typography, Grid, TextField, Button } from '@material-ui/core';
import styleForm from './estiloForm';
import IconoFormulario from "@material-ui/icons/SupervisedUserCircle"
import {compose} from "recompose";
import {consumerFirebase} from "../../server";
const UsuarioInicial={
        nombre:"",
        apellido:"",
        correo:"",
        contrase単a:""
}
class RegistrarEstudiante extends Component {
    state={
        Firebase:null,
        usuario:{
            nombre:"",
            apellido:"",
            correo:"",
            contrase単a:"",
            tipo:"Estudiante"
        }
    }
    static getDerivedStateFromProps(nextProps,prevState){
        if(nextProps.Firebase===prevState.Firebase){
            return null;
        }
        return{
            Firebase:nextProps.Firebase
        }
    }
    guardarEstado= e=>{
        let UsuarioEstudiante= Object.assign({},this.state.usuario);
        UsuarioEstudiante[e.target.name] =e.target.value;
        this.setState({
            usuario:UsuarioEstudiante
        })
    }
    registrarEstudiante=e=>{
        e.preventDefault();
        console.log("Usuario", this.state.usuario);
        const {usuario,Firebase}=this.state;
        firebase.db
        .collection("Usuarios")
        .add(usuario)
        .then(usuarioDespues=>{
                console.log("Insercion Echa "+usuarioDespues)
                this.setState({
                    usuario:UsuarioInicial
                })
        }).catch(error=>{
            console.log("Error "+error)
        })

    }
    render() {
        return (
           <Container maxWidth="md">
               <div style={styleForm.paper}>
                    <Avatar style={styleForm.Avatar}>
                        <IconoFormulario/>
                    </Avatar>
                    <br></br>
                    <Typography component="h1" variant="h5" >Crea una cuenta Estudiante</Typography>
                    <form style={styleForm.formulario}> 
                        <Grid container spacing={2} >
                                <Grid item md={12} xs={12}> 
                                    <TextField name="nombre" onChange={this.guardarEstado} value={this.state.usuario.nombre} fullWidth label="Ingrese su nombre"/>
                                </Grid>
                                <Grid item md={12} xs={12}> 
                                    <TextField name="apellido" onChange={this.guardarEstado} value={this.state.usuario.apellido}fullWidth label="Ingrese su apellido"/>
                                </Grid>
                                <Grid item md={12} xs={12}> 
                                    <TextField name="correo" onChange={this.guardarEstado} value={this.state.usuario.correo} fullWidth label="Ingrese su correo"/>
                                </Grid>
                                <Grid item md={12} xs={12}> 
                                    <TextField name="contrase単a" onChange={this.guardarEstado} value={this.state.usuario.contrase単a} type="password" fullWidth label="Ingrese su contrase単a"/>
                                </Grid>
                        </Grid>
                        <Grid container justify="center">
                            <Grid item xs={12} md={12}>
                                <Button onClick={this.registrarEstudiante}type="submit" variant="contained" fullWidth size="large" color ="primary" style={styleForm.submit}>Registrarse</Button>
                            </Grid>
                        </Grid>
                    </form>
               </div>
               <br></br>
               <br></br>
               <br></br>
               <br></br>
           </Container>
        );
    }
}

export default compose(consumerFirebase)(RegistrarEstudiante);
